print(" Atbash Cipher")

def atbash (text) :
    result = ""
    for ch in text:
        if ch.isupper():
            result+=chr(ord('A')+(ord('Z')-ord(ch)))
        elif ch.islower():
            result+=chr(ord('a')+(ord('z')-ord(ch)))                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  
            result +=ch
    return result

inp=input("Enter text to Encrypt :")
encrypt=atbash(inp)
print("Encrrypted Text :" , encrypt)
